import {Box, Paper, TableBody, TableCell, TableContainer, TableHead, TableRow} from "@mui/material";

const DataTable = ({tableRows, values,changeHandler}) => {
    return (
        <Box>
            {/* File Uploader */}
            <input
                type="file"
                name="file"
                onChange={changeHandler}
                accept=".csv"
                style={{ display: "block", margin: "10px auto" }}
            />
            <br />
            <br />

            <TableContainer component={Paper}>
                <DataTable sx={{ minWidth: 650 }} aria-label="simple table">
                    <TableHead style={{backgroundColor:"#d3a6ff", color:"white"}}>
                        <TableRow>
                            {tableRows.map((rows, index) => {
                                return <TableCell key={index}>{rows}</TableCell>;
                            })}
                        </TableRow>
                    </TableHead>

                    <TableBody>

                        {values.map((value, index) => {
                            return (
                                <TableRow key={index}>
                                    {value.map((val, i) => {
                                        return <TableCell key={i}>{val}</TableCell>;
                                    })}
                                </TableRow>
                            );
                        })}
                    </TableBody>


                </DataTable>
            </TableContainer>
        </Box>
    )
}

export default DataTable
