import { useState } from "react";
import Papa from "papaparse";
import {
    Box, Button,
    Input,
    Paper, PropTypes,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead, TablePagination,
    TableRow, Typography
} from "@mui/material";
import FileUploadIcon from '@mui/icons-material/FileUpload';
import {makeStyles} from "@mui/styles";
//import { DataGrid } from '@mui/x-data-grid';


const useStyles = makeStyles({
    body: {
        display: "flex",
        justifyContent:"center",
        alignItems:"center",
        padding:"16px",
        paddingInline: "20px",
        flexDirection:"column",
        backgroundColor :"rgba(172,191,255,0.62)",
        height:"100%",
        width: "100%",
        boxSizing:"border-box"
    },

    title : {
        padding: "10px",
        display: "flex",
        justifyContent:"center",
        alignItems:"center",
        flexDirection:"column"

    },

    inputBlock: {
        display: "flex",
        justifyContent: "center",
        margin: "10px auto",
        padding:"30px",
        backgroundColor:"#b9ffc8",
        borderRadius:"8px"
    },

    tableHeader :{
        backgroundColor :"#acbfff",
        color: "#fff !important"
    }
});



// DataGrid.propTypes = {
//     columns: PropTypes.any,
//     checkboxSelection: PropTypes.bool,
//     rowsPerPageOptions: PropTypes.arrayOf(PropTypes.number),
//     pageSize: PropTypes.number,
//     rows: PropTypes.number
// };

function App() {
    const classes = useStyles();
    const [parsedData, setParsedData] = useState([]);
    const [tableRows, setTableRows] = useState([]);
    const [values, setValues] = useState([]);
    const [rowsPerPage, setRowsPerPage] = useState(5);
    const [page, setPage] = useState(0);


    const handleChangeRowsPerPage = (event) => {
        setRowsPerPage(parseInt(event.target.value, 10));
        setPage(0);
    };


    const handleChangePage = (event, newPage) => {
        setPage(newPage);
    };

    console.log(values.length)
    console.log(parsedData)



    const changeHandler = (event) => {
        // Passing file data (event.target.files[0]) to parse using Papa.parse
        Papa.parse(event.target.files[0], {
            header: true,
            skipEmptyLines: true,
            complete: function (results) {
                const rowsArray = [];
                const valuesArray = [];

                // Iterating data to get column name and their values
                results.data.map((d) => {
                    rowsArray.push(Object.keys(d));
                    valuesArray.push(Object.values(d));
                });

                // Parsed Data Response in array format
                setParsedData(results.data);

                // Filtered Column Names
                setTableRows(rowsArray[0]);

                // Filtered Values
                setValues(valuesArray);
            },
        });
    };

    return (
        <Box className={classes.body}>

            <Box className={classes.title}>
            <Typography variant="h3">
                CSV File Visualizer
            </Typography>
            {/* File Uploader */}
            <Button
                component="label"
                variant="outlined"
                startIcon={<FileUploadIcon />}
            >
                Upload File
                <input
                    type="file"
                    onChange={changeHandler}
                    accept=".csv"
                    hidden
                />
            </Button>
            </Box>

            {/* DataTable */}

            { (values.length = 0) ?
                <Box>
                    <Typography>file is empty</Typography>
            </Box> :             <>
                <TableContainer style={{ height: 600, width: '100%' }} component={Paper}>
                    <Table stickyHeader aria-label="sticky table">
                        <TableHead>
                            <TableRow className={classes.tableHeader}>
                                {tableRows.map((rows, index) => {
                                    return <TableCell key={index}>{rows}</TableCell>;
                                })}
                            </TableRow>
                        </TableHead>

                        <TableBody>

                            {values.map((value, index) => {
                                return (
                                    <TableRow key={index}>
                                        {value.map((val, i) => {
                                            return <TableCell style={{ maxWidth:400}} key={i}>{val}</TableCell>;
                                        })}
                                    </TableRow>
                                );
                            })}
                        </TableBody>


                    </Table>
                </TableContainer>

                <TablePagination
                    rowsPerPageOptions={[5, 10, 25]}
                    component="div"
                    count={parsedData.length}
                    rowsPerPage={rowsPerPage}
                    page={page}
                    onPageChange={handleChangePage}
                    onRowsPerPageChange={handleChangeRowsPerPage}
                />
            </>
            }



        </Box>
    );
}

export default App;
